# Step 1: Check if openpyxl is installed, if not, install it.
import subprocess
import pkg_resources

required_package = 'openpyxl'
installed_packages = {pkg.key for pkg in pkg_resources.working_set}

if required_package not in installed_packages:
    print(f"{required_package} not installed. Installing now.")
    subprocess.run(["pip", "install", required_package], check=True)
else:
    print(f"{required_package} is already installed.")
